import React, { useState, useEffect } from "react";
import axios from "axios";

const WeatherHistory = () => {
    const [records, setRecords] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchType, setSearchType] = useState("recent");
    const [loading, setLoading] = useState(false);
    const [editingRecord, setEditingRecord] = useState(null);
    const [newRecord, setNewRecord] = useState({ location: "", temperature: "", weather: "", wind_speed: "", humidity: "" });

    useEffect(() => {
        fetchWeatherHistory();
    }, []);

    const fetchWeatherHistory = async () => {
        setLoading(true);
        try {
            let url = "http://localhost:5000/api/weather/history";

            if (searchType !== "recent" && searchTerm.trim() !== "") {
                url = `http://localhost:5000/api/weather/history?${searchType}=${searchTerm}`;
            }

            const response = await axios.get(url);
            setRecords(response.data);
        } catch (error) {
            alert("Failed to fetch weather history.");
        }
        setLoading(false);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this record?")) {
            try {
                await axios.delete(`http://localhost:5000/api/weather/${id}`);
                fetchWeatherHistory();
            } catch (error) {
                alert("Failed to delete the record.");
            }
        }
    };

    const handleEdit = (record) => {
        setEditingRecord(record);
    };

    const handleUpdate = async () => {
        if (!editingRecord) return;

        try {
            await axios.put(`http://localhost:5000/api/weather/${editingRecord._id}`, editingRecord);
            setEditingRecord(null);
            fetchWeatherHistory();
        } catch (error) {
            alert("Failed to update the record.");
        }
    };

    const handleAdd = async () => {
        try {
            await axios.post(`http://localhost:5000/api/weather`, newRecord);
            setNewRecord({ location: "", temperature: "", weather: "", wind_speed: "", humidity: "" });
            fetchWeatherHistory();
        } catch (error) {
            alert("Failed to add a new record.");
        }
    };

    return (
        <div>
            <h2>Weather History</h2>

            <div>
                <select value={searchType} onChange={(e) => setSearchType(e.target.value)}>
                    <option value="recent">Recent 10</option>
                    <option value="user">By Username</option>
                    <option value="date">By Date</option>
                    <option value="location">By Location</option>
                </select>
                <input type="text" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} placeholder="Enter search term" disabled={searchType === "recent"} />
                <button onClick={fetchWeatherHistory}>Search</button>
            </div>

            <h3>Add New Record</h3>
            <input type="text" placeholder="Location" value={newRecord.location} onChange={(e) => setNewRecord({ ...newRecord, location: e.target.value })} />
            <input type="text" placeholder="Weather" value={newRecord.weather} onChange={(e) => setNewRecord({ ...newRecord, weather: e.target.value })} />
            <input type="number" placeholder="Temperature (°C)" value={newRecord.temperature} onChange={(e) => setNewRecord({ ...newRecord, temperature: e.target.value })} />
            <input type="number" placeholder="Wind Speed (km/h)" value={newRecord.wind_speed} onChange={(e) => setNewRecord({ ...newRecord, wind_speed: e.target.value })} />
            <input type="number" placeholder="Humidity (%)" value={newRecord.humidity} onChange={(e) => setNewRecord({ ...newRecord, humidity: e.target.value })} />
            <button onClick={handleAdd}>Add</button>

            {loading ? <p>Loading...</p> : (
                <table border="1">
                    <thead>
                        <tr>
                            <th>Queried At</th>
                            <th>Location</th>
                            <th>Weather</th>
                            <th>Temperature (°C)</th>
                            <th>Wind Speed (km/h)</th>
                            <th>Humidity (%)</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {records.length > 0 ? (
                            records.map((record) => (
                                <tr key={record._id}>
                                    <td>{new Date(record.queriedAt).toLocaleString()}</td>
                                    <td>{record.location || "Unknown"}</td>
                                    <td>{record.current_weather?.weather || "N/A"}</td>
                                    <td>{record.current_weather?.temperature ?? "N/A"}</td>
                                    <td>{record.current_weather?.wind_speed ?? "N/A"}</td>
                                    <td>{record.current_weather?.humidity ?? "N/A"}</td>
                                    <td>
                                        <button onClick={() => handleEdit(record)}>✏️ Edit</button>
                                        <button onClick={() => handleDelete(record._id)}>🗑️ Delete</button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="7">No records found.</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            )}

            {editingRecord && (
                <div>
                    <h3>Edit Record</h3>
                    <input type="text" value={editingRecord.location} onChange={(e) => setEditingRecord({ ...editingRecord, location: e.target.value })} />
                    <input type="text" value={editingRecord.current_weather?.weather} onChange={(e) => setEditingRecord({ ...editingRecord, current_weather: { ...editingRecord.current_weather, weather: e.target.value } })} />
                    <input type="number" value={editingRecord.current_weather?.temperature} onChange={(e) => setEditingRecord({ ...editingRecord, current_weather: { ...editingRecord.current_weather, temperature: e.target.value } })} />
                    <input type="number" value={editingRecord.current_weather?.wind_speed} onChange={(e) => setEditingRecord({ ...editingRecord, current_weather: { ...editingRecord.current_weather, wind_speed: e.target.value } })} />
                    <input type="number" value={editingRecord.current_weather?.humidity} onChange={(e) => setEditingRecord({ ...editingRecord, current_weather: { ...editingRecord.current_weather, humidity: e.target.value } })} />
                    <button onClick={handleUpdate}>Save</button>
                    <button onClick={() => setEditingRecord(null)}>Cancel</button>
                </div>
            )}
        </div>
    );
};

export default WeatherHistory;
