import React, { useState } from "react";
import SearchBar from "../components/SearchBar";
import axios from "axios";

const Home = ({ user }) => {
    const [weatherData, setWeatherData] = useState(null);
    const [aiSuggestion, setAiSuggestion] = useState("");

    // get Ai suggestion
    const fetchAiSuggestion = async () => {
        if (!weatherData) {
            alert("Please fetch the weather data first.");
            return;
        }

        const weatherPrompt = `
            Current Weather: ${weatherData.current_weather.weather}, Temperature: ${weatherData.current_weather.temperature}°C, Wind Speed: ${weatherData.current_weather.wind_speed} km/h, Humidity: ${weatherData.current_weather.humidity}%
            Past Weather: 
            ${weatherData.past_weather.map((day) => `${day.date}: Max Temp ${day.temperature_max}°C, Min Temp ${day.temperature_min}°C, Precipitation ${day.precipitation}mm`).join("\n")}
            Future Forecast:
            ${weatherData.future_weather.map((day) => `${day.date}: ${day.weather}, Temperature: ${day.temperature}°C, Wind Speed: ${day.wind_speed} km/h, Humidity: ${day.humidity}%`).join("\n")}
        `;

        setLoadingAi(true);
        console.log("Sending AI Request with Prompt:", weatherPrompt);

        try {
            const response = await axios.post("http://localhost:5000/api/ai-suggestion", {
                prompt: weatherPrompt
            });

            console.log("AI Response:", response.data);
            setAiSuggestion(response.data.suggestion);
        } catch (error) {
            console.error("Error fetching AI suggestion:", error);
            alert("Failed to get AI suggestion.");
        }
        setLoadingAi(false);
    };

    return (
        <div style={styles.container}>
            <h1>Weather App</h1>
            <SearchBar setWeatherData={setWeatherData} />

            {weatherData && weatherData.current_weather ? (
                <div>
                    <h2>{weatherData.location || "Unknown Location"}'s Weather</h2>
                    <table style={styles.table}>
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Weather</th>
                                <th>Temperature (°C)</th>
                                <th>Wind Speed (km/h)</th>
                                <th>Humidity (%)</th>
                            </tr>
                        </thead>
                        <tbody>
                            {/* Current Weather */}
                            <tr>
                                <td>Now</td>
                                <td>{weatherData.current_weather.weather}</td>
                                <td>{weatherData.current_weather.temperature}°C</td>
                                <td>{weatherData.current_weather.wind_speed} km/h</td>
                                <td>{weatherData.current_weather.humidity}%</td>
                            </tr>
                            {/* Past Weather */}
                            {weatherData.past_weather.map((day, index) => (
                                <tr key={index}>
                                    <td>{day.date} (Past)</td>
                                    <td>Code: {day.weather_code}</td>
                                    <td>{day.temperature_max} / {day.temperature_min}°C</td>
                                    <td>N/A</td>
                                    <td>N/A</td>
                                </tr>
                            ))}
                            {/* Future Weather */}
                            {weatherData.future_weather.map((day, index) => (
                                <tr key={index}>
                                    <td>{day.date} (Future)</td>
                                    <td>{day.weather}</td>
                                    <td>{day.temperature}°C</td>
                                    <td>{day.wind_speed} km/h</td>
                                    <td>{day.humidity}%</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {/* AI Suggestion Section */}
                    <div style={styles.aiContainer}>
                        <h3>💡 AI Suggestion</h3>
                        <button onClick={fetchAiSuggestion} style={styles.button}>Generate AI Insights</button>
                        {aiSuggestion && <p style={styles.aiText}>{aiSuggestion}</p>}
                    </div>
                </div>
            ) : (
                <p>Enter coordinates and fetch weather data.</p>
            )}
        </div>
    );
};

// Styles
const styles = {
    container: {
        textAlign: "center",
        padding: "20px",
    },
    table: {
        width: "80%",
        margin: "auto",
        borderCollapse: "collapse",
        marginBottom: "20px",
    },
    aiContainer: {
        marginTop: "20px",
        padding: "10px",
        border: "1px solid #ccc",
        borderRadius: "5px",
        background: "#f9f9f9",
        display: "inline-block",
    },
    aiText: {
        fontStyle: "italic",
        marginTop: "10px",
    },
    button: {
        margin: "5px",
        padding: "8px",
        cursor: "pointer",
    },
};

export default Home;
