import React, { useState } from "react";
import SearchBar from "../components/SearchBar";
import axios from "axios";

const Home = ({ user }) => {
    const [weatherData, setWeatherData] = useState(null);
    const [aiSuggestion, setAiSuggestion] = useState("");
    const [loadingAi, setLoadingAi] = useState(false);
    const [locationInput, setLocationInput] = useState(""); // 地名输入框
    const [loadingLocation, setLoadingLocation] = useState(false);

    // 🌍 通过地名获取经纬度
    const fetchCoordinates = async () => {
        if (!locationInput.trim()) {
            alert("Please enter a location.");
            return;
        }

        setLoadingLocation(true);
        try {
            const response = await axios.get("http://localhost:5000/api/weather/get-location", {
                params: { location: locationInput }
            });

            console.log("📍 Location Coordinates:", response.data);
            fetchWeatherByCoords(response.data.lat, response.data.lon); // 用获取到的经纬度查询天气
        } catch (error) {
            console.error("❌ Error fetching coordinates:", error);
            alert("Failed to get location coordinates.");
        }
        setLoadingLocation(false);
    };

    // 🌤️ 获取天气数据
    const fetchWeatherByCoords = async (lat, lon) => {
        try {
            const response = await axios.post("http://localhost:5000/api/weather/get-weather", {
                lat,
                lon,
                lang: "en",
                userId: user?.id || "default-user",
            });

            console.log("✅ Weather Data:", response.data);
            setWeatherData(response.data);
        } catch (error) {
            console.error("❌ Error fetching weather data:", error);
            alert("Failed to fetch weather data.");
        }
    };

    // 🧠 获取 AI 建议
    const fetchAiSuggestion = async () => {
        if (!weatherData) {
            alert("Please fetch the weather data first.");
            return;
        }

        const weatherPrompt = `
            Current Weather: ${weatherData.current_weather.weather}, 
            Temperature: ${weatherData.current_weather.temperature}°C, 
            Wind Speed: ${weatherData.current_weather.wind_speed} km/h, 
            Humidity: ${weatherData.current_weather.humidity}%

            Past Weather: 
            ${weatherData.past_weather.map((day) => 
                `${day.date}: Max Temp ${day.temperature_max}°C, Min Temp ${day.temperature_min}°C, Precipitation ${day.precipitation}mm`
            ).join("\n")}

            Future Forecast:
            ${weatherData.future_weather.map((day) => 
                `${day.date}: ${day.weather}, Temperature: ${day.temperature}°C, Wind Speed: ${day.wind_speed} km/h, Humidity: ${day.humidity}%`
            ).join("\n")}
        `;

        setLoadingAi(true);
        console.log("Sending AI Request with Prompt:", weatherPrompt);

        try {
            const response = await axios.post("http://localhost:5000/api/weather/ai-suggestion", {
                prompt: weatherPrompt
            });

            console.log("✅ AI Response:", response.data);
            setAiSuggestion(response.data.suggestion);
        } catch (error) {
            console.error("❌ Error fetching AI suggestion:", error);
            alert("Failed to get AI suggestion.");
        }
        setLoadingAi(false);
    };

    return (
        <div style={styles.container}>
            <h1>Weather App</h1>

            {/* 🏙️ 地名搜索框 */}
            <div style={styles.locationContainer}>
                <input 
                    type="text" 
                    placeholder="Enter a location (e.g., New York, Paris)" 
                    value={locationInput} 
                    onChange={(e) => setLocationInput(e.target.value)} 
                    style={styles.input} 
                />
                <button onClick={fetchCoordinates} style={styles.button} disabled={loadingLocation}>
                    {loadingLocation ? "Loading..." : "Search by Location"}
                </button>
            </div>

            {/* 🔍 旧的 SearchBar (经纬度查询) */}
            <SearchBar setWeatherData={setWeatherData} />

            {weatherData && weatherData.current_weather ? (
                <div>
                    <h2>{weatherData.location || "Unknown Location"}'s Weather</h2>
                    <table style={styles.table}>
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Weather</th>
                                <th>Temperature (°C)</th>
                                <th>Wind Speed (km/h)</th>
                                <th>Humidity (%)</th>
                            </tr>
                        </thead>
                        <tbody>
                            {/* Current Weather */}
                            <tr>
                                <td><b>Now</b></td>
                                <td>{weatherData.current_weather.weather || "N/A"}</td>
                                <td>{weatherData.current_weather.temperature ?? "N/A"}°C</td>
                                <td>{weatherData.current_weather.wind_speed ?? "N/A"} km/h</td>
                                <td>{weatherData.current_weather.humidity ?? "N/A"}%</td>
                            </tr>
                            {/* Past Weather */}
                            {weatherData.past_weather.map((day, index) => (
                                <tr key={index}>
                                    <td>{day.date} (Past)</td>
                                    <td>{day.weather_code ? `Code: ${day.weather_code}` : "N/A"}</td>
                                    <td>{day.temperature_max ?? "N/A"} / {day.temperature_min ?? "N/A"}°C</td>
                                    <td>N/A</td>
                                    <td>N/A</td>
                                </tr>
                            ))}
                            {/* Future Weather */}
                            {weatherData.future_weather.map((day, index) => (
                                <tr key={index}>
                                    <td>{day.date} (Future)</td>
                                    <td>{day.weather || "N/A"}</td>
                                    <td>{day.temperature ?? "N/A"}°C</td>
                                    <td>{day.wind_speed ?? "N/A"} km/h</td>
                                    <td>{day.humidity ?? "N/A"}%</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {/* AI Suggestion Section */}
                    <div style={styles.aiContainer}>
                        <h3>💡 AI Suggestion</h3>
                        <button onClick={fetchAiSuggestion} style={styles.button} disabled={loadingAi}>
                            {loadingAi ? "Generating..." : "Generate AI Insights"}
                        </button>
                        {aiSuggestion && <p style={styles.aiText}>{aiSuggestion}</p>}
                    </div>
                </div>
            ) : (
                <p>Enter a location or coordinates and fetch weather data.</p>
            )}
        </div>
    );
};

// Styles
const styles = {
    container: {
        textAlign: "center",
        padding: "20px",
    },
    locationContainer: {
        marginBottom: "15px",
    },
    input: {
        padding: "8px",
        marginRight: "10px",
        width: "250px",
    },
    table: {
        width: "80%",
        margin: "auto",
        borderCollapse: "collapse",
        marginBottom: "20px",
        border: "1px solid #ddd",
    },
    aiContainer: {
        marginTop: "20px",
        padding: "10px",
        border: "1px solid #ccc",
        borderRadius: "5px",
        background: "#f9f9f9",
        display: "inline-block",
    },
    aiText: {
        fontStyle: "italic",
        marginTop: "10px",
    },
    button: {
        margin: "5px",
        padding: "8px",
        cursor: "pointer",
        backgroundColor: "#007bff",
        color: "white",
        border: "none",
        borderRadius: "5px",
    },
};

export default Home;
