import React, { useState, useEffect } from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import { jsPDF } from "jspdf";

const API_BASE = "http://localhost:5000/api/weather";

const WeatherHistory = () => {
    const [records, setRecords] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchType, setSearchType] = useState("recent");
    const [loading, setLoading] = useState(false);
    const [editingRecord, setEditingRecord] = useState(null);

    useEffect(() => {
        fetchWeatherHistory();
    }, []);

    const fetchWeatherHistory = async () => {
        setLoading(true);
        try {
            let url = `${API_BASE}/history`;
            if (searchType !== "recent" && searchTerm.trim() !== "") {
                url = `${API_BASE}/history?${searchType}=${searchTerm}`;
            }
            const response = await axios.get(url);
            setRecords(response.data);
        } catch (error) {
            alert("Failed to fetch weather history.");
        }
        setLoading(false);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this record?")) {
            try {
                await axios.delete(`${API_BASE}/${id}`);
                fetchWeatherHistory();
            } catch (error) {
                alert("Failed to delete the record.");
            }
        }
    };

    const handleEdit = (record) => {
        setEditingRecord({ ...record });
    };

    const cancelEdit = () => {
        setEditingRecord(null);
    };

    const handleUpdate = async () => {
        if (!editingRecord) return;
        try {
            await axios.put(`${API_BASE}/${editingRecord._id}`, editingRecord);
            setEditingRecord(null);
            fetchWeatherHistory();
        } catch (error) {
            alert("Failed to update the record.");
        }
    };

    const handleInputChange = (e, field) => {
        setEditingRecord({ ...editingRecord, [field]: e.target.value });
    };

    // Export JSON
    const exportJSON = () => {
        const blob = new Blob([JSON.stringify(records, null, 2)], { type: "application/json" });
        saveAs(blob, "weather_data.json");
    };

    // Export CSV
    const exportCSV = () => {
        if (records.length === 0) {
            alert("No data to export.");
            return;
        }
        let csvContent = "Queried At,Location,Weather,Temperature (°C),Wind Speed (km/h),Humidity (%)\n";
        records.forEach(record => {
            csvContent += `${new Date(record.queriedAt).toLocaleString()},${record.location},${record.current_weather?.weather || "N/A"},${record.current_weather?.temperature || "N/A"},${record.current_weather?.wind_speed || "N/A"},${record.current_weather?.humidity || "N/A"}\n`;
        });
        const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
        saveAs(blob, "weather_data.csv");
    };

    // Export PDF
    const exportPDF = () => {
        const doc = new jsPDF();
        doc.text("Weather History", 10, 10);
        let y = 20;
        records.forEach((record, index) => {
            doc.text(`${index + 1}. ${record.location} - ${new Date(record.queriedAt).toLocaleString()}`, 10, y);
            doc.text(`Weather: ${record.current_weather?.weather || "N/A"}`, 10, y + 5);
            doc.text(`Temperature: ${record.current_weather?.temperature || "N/A"}°C`, 10, y + 10);
            doc.text(`Wind Speed: ${record.current_weather?.wind_speed || "N/A"} km/h`, 10, y + 15);
            doc.text(`Humidity: ${record.current_weather?.humidity || "N/A"}%`, 10, y + 20);
            y += 30;
        });
        doc.save("weather_data.pdf");
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Weather History</h2>

            {/* Export Buttons */}
            <div style={styles.searchContainer}>
                <button onClick={exportCSV} style={styles.button}>Export CSV</button>
                <button onClick={exportJSON} style={styles.button}>Export JSON</button>
                <button onClick={exportPDF} style={styles.button}>Export PDF</button>
            </div>

            {/* Weather Data Table */}
            {loading ? <p>Loading...</p> : (
                <table style={styles.table}>
                    <thead>
                        <tr>
                            <th>Queried At</th>
                            <th>Location</th>
                            <th>Weather</th>
                            <th>Temperature (°C)</th>
                            <th>Wind Speed (km/h)</th>
                            <th>Humidity (%)</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {records.map((record) => (
                            <tr key={record._id}>
                                <td>{new Date(record.queriedAt).toLocaleString()}</td>
                                <td>
                                    {editingRecord?._id === record._id ? (
                                        <input type="text" value={editingRecord.location} onChange={(e) => handleInputChange(e, "location")} style={styles.input} />
                                    ) : (
                                        record.location
                                    )}
                                </td>
                                <td>{record.current_weather?.weather || "N/A"}</td>
                                <td>{record.current_weather?.temperature ? `${record.current_weather.temperature}°C` : "N/A"}</td>
                                <td>{record.current_weather?.wind_speed ? `${record.current_weather.wind_speed} km/h` : "N/A"}</td>
                                <td>{record.current_weather?.humidity ? `${record.current_weather.humidity}%` : "N/A"}</td>
                                <td>
                                    {editingRecord?._id === record._id ? (
                                        <>
                                            <button onClick={handleUpdate} style={styles.buttonSave}>Save</button>
                                            <button onClick={cancelEdit} style={styles.buttonCancel}>Cancel</button>
                                        </>
                                    ) : (
                                        <button onClick={() => handleEdit(record)} style={styles.button}>Edit</button>
                                    )}
                                    <button onClick={() => handleDelete(record._id)} style={styles.button}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

// Styles
const styles = {
    container: { textAlign: "center", padding: "20px" },
    title: { fontSize: "24px", fontWeight: "bold" },
    searchContainer: { marginBottom: "20px" },
    table: { width: "100%", borderCollapse: "collapse", marginTop: "20px" },
    input: { padding: "5px", border: "1px solid #ccc", borderRadius: "4px" },
    button: { padding: "5px 10px", cursor: "pointer", margin: "5px" },
    buttonSave: { backgroundColor: "green", color: "white" },
    buttonCancel: { backgroundColor: "red", color: "white" },
};

export default WeatherHistory;
