import React, { useState, useEffect } from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import { jsPDF } from "jspdf";

const WeatherHistory = () => {
    const [records, setRecords] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchType, setSearchType] = useState("recent");
    const [loading, setLoading] = useState(false);
    const [exportFormat, setExportFormat] = useState(""); 
    const [editingRecord, setEditingRecord] = useState(null);

    useEffect(() => {
        fetchWeatherHistory();
    }, []);

    // get history info
    const fetchWeatherHistory = async () => {
        setLoading(true);
        try {
            let url = "http://localhost:5000/api/weather/history";
            if (searchType !== "recent" && searchTerm.trim() !== "") {
                url = `http://localhost:5000/api/weather/history?${searchType}=${searchTerm}`;
            }
            const response = await axios.get(url);
            setRecords(response.data);
        } catch (error) {
            alert("Failed to fetch weather history.");
        }
        setLoading(false);
    };

    // delete record
    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this record?")) {
            try {
                await axios.delete(`http://localhost:5000/api/weather/${id}`);
                fetchWeatherHistory();
            } catch (error) {
                alert("Failed to delete the record.");
            }
        }
    };

    // update mode
    const handleEdit = (record) => {
        console.log("Editing record:", record);
        setEditingRecord({ ...record });
    };

    // cancel updating
    const cancelEdit = () => {
        setEditingRecord(null);
    };

    // update record
    const handleUpdate = async () => {
        if (!editingRecord) return;

        try {
            await axios.put(`http://localhost:5000/api/weather/${editingRecord._id}`, editingRecord);
            setEditingRecord(null);
            fetchWeatherHistory();
        } catch (error) {
            alert("Failed to update the record.");
        }
    };

    // export json file
    const exportJSON = () => {
        const blob = new Blob([JSON.stringify(records, null, 2)], { type: "application/json" });
        saveAs(blob, "weather_data.json");
    };

    // export csv file
    const exportCSV = () => {
        if (records.length === 0) {
            alert("No data to export.");
            return;
        }

        let csvContent = "Queried At,Location,Weather,Temperature (°C),Wind Speed (km/h),Humidity (%)\n";
        records.forEach(record => {
            csvContent += `${new Date(record.queriedAt).toLocaleString()},${record.location},${record.current_weather.weather},${record.current_weather.temperature},${record.current_weather.wind_speed},${record.current_weather.humidity}\n`;
        });

        const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
        saveAs(blob, "weather_data.csv");
    };

    // export pdf file
    const exportPDF = () => {
        const doc = new jsPDF();
        doc.text("Weather History", 10, 10);
        let y = 20;

        records.forEach((record, index) => {
            doc.text(
                `${index + 1}. ${record.location} - ${new Date(record.queriedAt).toLocaleString()}`, 
                10, y
            );
            doc.text(`Weather: ${record.current_weather.weather}`, 10, y + 5);
            doc.text(`Temperature: ${record.current_weather.temperature}°C`, 10, y + 10);
            doc.text(`Wind Speed: ${record.current_weather.wind_speed} km/h`, 10, y + 15);
            doc.text(`Humidity: ${record.current_weather.humidity}%`, 10, y + 20);
            y += 30;
        });

        doc.save("weather_data.pdf");
    };

    // select export format
    const handleExport = (format) => {
        setExportFormat(format);
        if (format === "json") exportJSON();
        else if (format === "csv") exportCSV();
        else if (format === "pdf") exportPDF();
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Weather History</h2>

            {/* search bar */}
            <div style={styles.searchContainer}>
                <select value={searchType} onChange={(e) => setSearchType(e.target.value)} style={styles.input}>
                    <option value="recent">Recent 10</option>
                    <option value="user">By Username</option>
                    <option value="date">By Date</option>
                    <option value="location">By Location</option>
                </select>
                <input 
                    type="text" 
                    value={searchTerm} 
                    onChange={(e) => setSearchTerm(e.target.value)} 
                    placeholder="Enter search term" 
                    disabled={searchType === "recent"} 
                    style={styles.input}
                />
                <button onClick={fetchWeatherHistory} style={styles.button}>Search</button>
            </div>

            {/* dropdown menu */}
            <div style={styles.exportContainer}>
                <label>Export As: </label>
                <select onChange={(e) => handleExport(e.target.value)} style={styles.input}>
                    <option value="">Select Format</option>
                    <option value="json">JSON</option>
                    <option value="csv">CSV</option>
                    <option value="pdf">PDF</option>
                </select>
            </div>

            {/* history weather */}
            {loading ? <p>Loading...</p> : (
                <table style={styles.table}>
                    <thead>
                        <tr>
                            <th>Queried At</th>
                            <th>Location</th>
                            <th>Weather</th>
                            <th>Temperature (°C)</th>
                            <th>Wind Speed (km/h)</th>
                            <th>Humidity (%)</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {records.map((record) => (
                            <tr key={record._id}>
                                <td>{new Date(record.queriedAt).toLocaleString()}</td>
                                <td>{record.location}</td>
                                <td>{record.current_weather.weather}</td>
                                <td>{record.current_weather.temperature}°C</td>
                                <td>{record.current_weather.wind_speed} km/h</td>
                                <td>{record.current_weather.humidity}%</td>
                                <td>
                                    <button onClick={() => handleEdit(record)} style={styles.button}>Edit</button>
                                    <button onClick={() => handleDelete(record._id)} style={styles.button}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

const styles = {
    container: {
        textAlign: "center",
        padding: "20px",
    },
    title: {
        marginBottom: "20px",
    },
    searchContainer: {
        marginBottom: "20px",
    },
    exportContainer: {
        marginBottom: "20px",
    },
    table: {
        width: "100%",
        borderCollapse: "collapse",
        marginTop: "20px",
        border: "1px solid #ddd",
    },
    input: {
        margin: "5px",
        padding: "8px",
    },
    button: {
        margin: "5px",
        padding: "8px",
        cursor: "pointer",
        backgroundColor: "#007bff",
        color: "white",
        border: "none",
        borderRadius: "5px",
    },
};

export default WeatherHistory;
