import React, { useState, useEffect } from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import { jsPDF } from "jspdf";

const WeatherHistory = () => {
    const [records, setRecords] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchType, setSearchType] = useState("recent");
    const [loading, setLoading] = useState(false);
    const [editingRecord, setEditingRecord] = useState(null);
    const [newRecord, setNewRecord] = useState({
        location: "",
        weather: "",
        temperature: "",
        wind_speed: "",
        humidity: ""
    });
    const [showAddForm, setShowAddForm] = useState(false);

    useEffect(() => {
        fetchWeatherHistory();
    }, []);

    // Fetch weather history from the API
    const fetchWeatherHistory = async () => {
        setLoading(true);
        try {
            let url = "http://localhost:5000/api/weather/history";
            if (searchType !== "recent" && searchTerm.trim() !== "") {
                url = `http://localhost:5000/api/weather/history?${searchType}=${searchTerm}`;
            }
            const response = await axios.get(url);
            setRecords(response.data);
        } catch (error) {
            alert("Failed to fetch weather history.");
        }
        setLoading(false);
    };

    // Delete a weather record
    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this record?")) {
            try {
                await axios.delete(`http://localhost:5000/api/weather/${id}`);
                fetchWeatherHistory();
            } catch (error) {
                alert("Failed to delete the record.");
            }
        }
    };

    // Enable edit mode for a record
    const handleEdit = (record) => {
        setEditingRecord({ ...record });
    };

    // Cancel edit mode
    const cancelEdit = () => {
        setEditingRecord(null);
    };

    // Update an existing weather record
    const handleUpdate = async () => {
        if (!editingRecord) return;

        try {
            await axios.put(`http://localhost:5000/api/weather/${editingRecord._id}`, editingRecord);
            setEditingRecord(null);
            fetchWeatherHistory();
        } catch (error) {
            alert("Failed to update the record.");
        }
    };

    // Add a new weather record
    const handleAddRecord = async () => {
        if (!newRecord.location || !newRecord.weather || !newRecord.temperature) {
            alert("Please fill in all required fields.");
            return;
        }

        try {
            await axios.post("http://localhost:5000/api/weather", newRecord);
            setShowAddForm(false);
            setNewRecord({
                location: "",
                weather: "",
                temperature: "",
                wind_speed: "",
                humidity: ""
            });
            fetchWeatherHistory();
        } catch (error) {
            alert("Failed to add new record.");
        }
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Weather History</h2>

            {/* Search and Add Record Section */}
            <div style={styles.actionContainer}>
                {/* Search Bar */}
                <div style={styles.searchContainer}>
                    <select value={searchType} onChange={(e) => setSearchType(e.target.value)} style={styles.input}>
                        <option value="recent">Recent 10</option>
                        <option value="user">By Username</option>
                        <option value="date">By Date</option>
                        <option value="location">By Location</option>
                    </select>
                    <input 
                        type="text" 
                        value={searchTerm} 
                        onChange={(e) => setSearchTerm(e.target.value)} 
                        placeholder="Enter search term" 
                        disabled={searchType === "recent"} 
                        style={styles.input}
                    />
                    <button onClick={fetchWeatherHistory} style={styles.button}>Search</button>
                </div>

                {/* Add Record Button */}
                <button onClick={() => setShowAddForm(!showAddForm)} style={styles.addButton}>
                    {showAddForm ? "Cancel" : "➕ Add Record"}
                </button>
            </div>

            {/* Add New Record Form */}
            {showAddForm && (
                <div style={styles.addRecordForm}>
                    <input 
                        type="text" 
                        placeholder="Location" 
                        value={newRecord.location} 
                        onChange={(e) => setNewRecord({ ...newRecord, location: e.target.value })} 
                        style={styles.input}
                    />
                    <input 
                        type="text" 
                        placeholder="Weather" 
                        value={newRecord.weather} 
                        onChange={(e) => setNewRecord({ ...newRecord, weather: e.target.value })} 
                        style={styles.input}
                    />
                    <input 
                        type="number" 
                        placeholder="Temperature (°C)" 
                        value={newRecord.temperature} 
                        onChange={(e) => setNewRecord({ ...newRecord, temperature: e.target.value })} 
                        style={styles.input}
                    />
                    <input 
                        type="number" 
                        placeholder="Wind Speed (km/h)" 
                        value={newRecord.wind_speed} 
                        onChange={(e) => setNewRecord({ ...newRecord, wind_speed: e.target.value })} 
                        style={styles.input}
                    />
                    <input 
                        type="number" 
                        placeholder="Humidity (%)" 
                        value={newRecord.humidity} 
                        onChange={(e) => setNewRecord({ ...newRecord, humidity: e.target.value })} 
                        style={styles.input}
                    />
                    <button onClick={handleAddRecord} style={styles.button}>Submit</button>
                </div>
            )}

            {/* Weather Records Table */}
            {loading ? <p>Loading...</p> : (
                <table style={styles.table}>
                    <thead>
                        <tr>
                            <th>Queried At</th>
                            <th>Location</th>
                            <th>Weather</th>
                            <th>Temperature (°C)</th>
                            <th>Wind Speed (km/h)</th>
                            <th>Humidity (%)</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {records.map((record) => (
                            <tr key={record._id}>
                                <td>{new Date(record.queriedAt).toLocaleString()}</td>
                                <td>{record.location}</td>
                                <td>{record.current_weather.weather}</td>
                                <td>{record.current_weather.temperature}°C</td>
                                <td>{record.current_weather.wind_speed} km/h</td>
                                <td>{record.current_weather.humidity}%</td>
                                <td>
                                    <button onClick={() => handleEdit(record)} style={styles.button}>Edit</button>
                                    <button onClick={() => handleDelete(record._id)} style={styles.button}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

// Styles
const styles = {
    container: { textAlign: "center", padding: "20px" },
    title: { marginBottom: "20px" },
    actionContainer: { display: "flex", justifyContent: "space-between", marginBottom: "20px" },
    searchContainer: { display: "inline-block" },
    input: { margin: "5px", padding: "8px" },
    button: { margin: "5px", padding: "8px", cursor: "pointer", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "5px" },
    addButton: { backgroundColor: "#28a745", color: "white", padding: "8px", borderRadius: "5px", cursor: "pointer" },
    addRecordForm: { margin: "20px 0", padding: "10px", border: "1px solid #ddd", borderRadius: "5px", display: "flex", flexDirection: "column", alignItems: "center" }
};

export default WeatherHistory;
