import React, { useState, useEffect } from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import { jsPDF } from "jspdf";

const WeatherHistory = () => {
    const [records, setRecords] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchType, setSearchType] = useState("recent");
    const [loading, setLoading] = useState(false);
    const [editingRecordId, setEditingRecordId] = useState(null); // 记录当前正在编辑的行ID
    const [tempRecord, setTempRecord] = useState({}); // 存储临时修改的记录

    useEffect(() => {
        fetchWeatherHistory();
    }, []);

    const fetchWeatherHistory = async () => {
        setLoading(true);
        try {
            let url = "http://localhost:5000/api/weather/history";
            if (searchType !== "recent" && searchTerm.trim() !== "") {
                url = `http://localhost:5000/api/weather/history?${searchType}=${searchTerm}`;
            }
            const response = await axios.get(url);
            setRecords(response.data);
        } catch (error) {
            alert("Failed to fetch weather history.");
        }
        setLoading(false);
    };

    // 编辑模式：点击“Edit”后，当前行变成可编辑状态
    const handleEdit = (record) => {
        setEditingRecordId(record._id);
        setTempRecord({ ...record }); // 复制原始数据到临时变量
    };

    // 取消编辑
    const cancelEdit = () => {
        setEditingRecordId(null);
        setTempRecord({});
    };

    // 保存修改
    const handleUpdate = async () => {
        try {
            await axios.put(`http://localhost:5000/api/weather/${tempRecord._id}`, tempRecord);
            setEditingRecordId(null); // 退出编辑模式
            fetchWeatherHistory(); // 重新获取数据
        } catch (error) {
            alert("Failed to update the record.");
        }
    };

    // 处理输入框变化
    const handleInputChange = (e, field) => {
        setTempRecord({ ...tempRecord, [field]: e.target.value });
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Weather History</h2>

            {loading ? <p>Loading...</p> : (
                <table style={styles.table}>
                    <thead>
                        <tr>
                            <th>Queried At</th>
                            <th>Location</th>
                            <th>Weather</th>
                            <th>Temperature (°C)</th>
                            <th>Wind Speed (km/h)</th>
                            <th>Humidity (%)</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {records.map((record) => (
                            <tr key={record._id}>
                                <td>{new Date(record.queriedAt).toLocaleString()}</td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="text"
                                            value={tempRecord.location}
                                            onChange={(e) => handleInputChange(e, "location")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        record.location
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="text"
                                            value={tempRecord.weather}
                                            onChange={(e) => handleInputChange(e, "weather")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        record.weather
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="number"
                                            value={tempRecord.temperature}
                                            onChange={(e) => handleInputChange(e, "temperature")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        `${record.temperature}°C`
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="number"
                                            value={tempRecord.wind_speed}
                                            onChange={(e) => handleInputChange(e, "wind_speed")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        `${record.wind_speed} km/h`
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="number"
                                            value={tempRecord.humidity}
                                            onChange={(e) => handleInputChange(e, "humidity")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        `${record.humidity}%`
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <>
                                            <button onClick={handleUpdate} style={styles.buttonSave}>Save</button>
                                            <button onClick={cancelEdit} style={styles.buttonCancel}>Cancel</button>
                                        </>
                                    ) : (
                                        <button onClick={() => handleEdit(record)} style={styles.buttonEdit}>Edit</button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

// 样式
const styles = {
    container: { textAlign: "center", padding: "20px" },
    title: { fontSize: "24px", fontWeight: "bold" },
    table: { width: "100%", borderCollapse: "collapse", marginTop: "20px" },
    input: { width: "100px", padding: "5px", border: "1px solid #ccc", borderRadius: "4px" },
    buttonEdit: { backgroundColor: "#007bff", color: "white", border: "none", padding: "5px 10px", cursor: "pointer", marginRight: "5px" },
    buttonSave: { backgroundColor: "#28a745", color: "white", border: "none", padding: "5px 10px", cursor: "pointer", marginRight: "5px" },
    buttonCancel: { backgroundColor: "#dc3545", color: "white", border: "none", padding: "5px 10px", cursor: "pointer" },
};

export default WeatherHistory;
