import React, { useState } from "react";
import SearchBar from "../components/SearchBar";

const Home = () => {
    const [weatherData, setWeatherData] = useState(null);

    return (
        <div>
            <h1>Weather App</h1>
            <SearchBar setWeatherData={setWeatherData} />

            {weatherData && weatherData.current_weather ? (
                <div>
                    <h2>{weatherData.location || "Unknown Location"}'s Weather</h2>
                    <p>Weather: {weatherData.current_weather?.weather || "N/A"}</p>
                    <p>Temperature: {weatherData.current_weather?.temperature || "N/A"}째C</p>
                    <p>Humidity: {weatherData.current_weather?.humidity || "N/A"}%</p>
                    <p>Wind Speed: {weatherData.current_weather?.wind_speed || "N/A"} km/h</p>

                    <h3>Past 3 Days Weather:</h3>
                    {weatherData.past_weather?.length > 0 ? (
                        weatherData.past_weather.map((day, index) => (
                            <div key={index}>
                                <p>{day.date}:</p>
                                <p>Max Temp: {day.temperature_max}째C</p>
                                <p>Min Temp: {day.temperature_min}째C</p>
                                <p>Precipitation: {day.precipitation} mm</p>
                                <p>Weather Code: {day.weather_code}</p>
                            </div>
                        ))
                    ) : (
                        <p>No past weather data available.</p>
                    )}

                    <h3>Future 2 Days Weather:</h3>
                    {weatherData.future_weather?.length > 0 ? (
                        weatherData.future_weather.map((day, index) => (
                            <div key={index}>
                                <p>{day.date}: {day?.weather || "N/A"}</p>
                                <p>Temperature: {day?.temperature || "N/A"}째C</p>
                                <p>Humidity: {day?.humidity || "N/A"}%</p>
                                <p>Wind Speed: {day?.wind_speed || "N/A"} km/h</p>
                            </div>
                        ))
                    ) : (
                        <p>No future weather data available.</p>
                    )}
                </div>
            ) : (
                <p>Enter coordinates and fetch weather data.</p>
            )}
        </div>
    );
};

export default Home;
