import React, { useState, useEffect } from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import { jsPDF } from "jspdf";

const API_BASE = "http://localhost:5000/api/weather";

const WeatherHistory = () => {
    const [records, setRecords] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchType, setSearchType] = useState("recent");
    const [loading, setLoading] = useState(false);
    const [editingRecordId, setEditingRecordId] = useState(null); 
    const [tempRecord, setTempRecord] = useState({}); 

    useEffect(() => {
        fetchWeatherHistory();
    }, []);

    const fetchWeatherHistory = async () => {
        setLoading(true);
        try {
            let url = `${API_BASE}/history`;
            if (searchType !== "recent" && searchTerm.trim() !== "") {
                url = `${API_BASE}/history?${searchType}=${searchTerm}`;
            }
            const response = await axios.get(url);
            setRecords(response.data);
        } catch (error) {
            alert("Failed to fetch weather history.");
        }
        setLoading(false);
    };

    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this record?")) {
            try {
                await axios.delete(`${API_BASE}/${id}`);
                fetchWeatherHistory();
            } catch (error) {
                alert("Failed to delete the record.");
            }
        }
    };

    const handleEdit = (record) => {
        setEditingRecordId(record._id);
        setTempRecord({ ...record });
    };

    const cancelEdit = () => {
        setEditingRecordId(null);
        setTempRecord({});
    };

    const handleUpdate = async () => {
        try {
            await axios.put(`${API_BASE}/${tempRecord._id}`, tempRecord);
            setEditingRecordId(null);
            fetchWeatherHistory();
        } catch (error) {
            alert("Failed to update the record.");
        }
    };

    const handleInputChange = (e, field) => {
        setTempRecord({ ...tempRecord, [field]: e.target.value });
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Weather History</h2>

            {loading ? <p>Loading...</p> : (
                <table style={styles.table}>
                    <thead>
                        <tr>
                            <th>Queried At</th>
                            <th>Location</th>
                            <th>Weather</th>
                            <th>Temperature (°C)</th>
                            <th>Wind Speed (km/h)</th>
                            <th>Humidity (%)</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {records.map((record) => (
                            <tr key={record._id}>
                                <td>{new Date(record.queriedAt).toLocaleString()}</td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="text"
                                            value={tempRecord.location}
                                            onChange={(e) => handleInputChange(e, "location")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        record.location
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="text"
                                            value={tempRecord.weather}
                                            onChange={(e) => handleInputChange(e, "weather")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        record.current_weather?.weather || "N/A"
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="number"
                                            value={tempRecord.temperature}
                                            onChange={(e) => handleInputChange(e, "temperature")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        `${record.current_weather?.temperature || "N/A"}°C`
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="number"
                                            value={tempRecord.wind_speed}
                                            onChange={(e) => handleInputChange(e, "wind_speed")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        `${record.current_weather?.wind_speed || "N/A"} km/h`
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <input
                                            type="number"
                                            value={tempRecord.humidity}
                                            onChange={(e) => handleInputChange(e, "humidity")}
                                            style={styles.input}
                                        />
                                    ) : (
                                        `${record.current_weather?.humidity || "N/A"}%`
                                    )}
                                </td>
                                <td>
                                    {editingRecordId === record._id ? (
                                        <>
                                            <button onClick={handleUpdate} style={styles.buttonSave}>Save</button>
                                            <button onClick={cancelEdit} style={styles.buttonCancel}>Cancel</button>
                                        </>
                                    ) : (
                                        <button onClick={() => handleEdit(record)} style={styles.buttonEdit}>Edit</button>
                                    )}
                                    <button onClick={() => handleDelete(record._id)} style={styles.buttonDelete}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

// 样式
const styles = {
    container: { textAlign: "center", padding: "20px" },
    title: { fontSize: "24px", fontWeight: "bold" },
    table: { width: "100%", borderCollapse: "collapse", marginTop: "20px" },
    input: { width: "100px", padding: "5px", border: "1px solid #ccc", borderRadius: "4px" },
    buttonEdit: { backgroundColor: "#007bff", color: "white", border: "none", padding: "5px 10px", cursor: "pointer", marginRight: "5px" },
    buttonSave: { backgroundColor: "#28a745", color: "white", border: "none", padding: "5px 10px", cursor: "pointer", marginRight: "5px" },
    buttonCancel: { backgroundColor: "#dc3545", color: "white", border: "none", padding: "5px 10px", cursor: "pointer", marginRight: "5px" },
    buttonDelete: { backgroundColor: "#ff4444", color: "white", border: "none", padding: "5px 10px", cursor: "pointer" },
};

export default WeatherHistory;
