import React, { useState } from "react";
import axios from "axios";

const SearchBar = ({ setWeatherData }) => {
    const [latitude, setLatitude] = useState("");
    const [longitude, setLongitude] = useState("");
    const [loading, setLoading] = useState(false);

    // Validate latitude and longitude range
    const isValidCoords = (lat, lon) => {
        if (isNaN(lat) || isNaN(lon)) {
            alert("Latitude and longitude must be numbers.");
            return false;
        }
        if (lat < -90 || lat > 90 || lon < -180 || lon > 180) {
            alert("Latitude must be between -90 and 90, and longitude must be between -180 and 180.");
            return false;
        }
        return true;
    };

    // Fetch weather data by coordinates
    const fetchWeatherByCoords = async (lat, lon) => {
        if (!lat || !lon) {
            alert("Please provide valid latitude and longitude!");
            return;
        }
    
        try {
            const response = await axios.post("http://localhost:5000/api/weather/get-weather", {
                lat: parseFloat(lat),
                lon: parseFloat(lon),
                lang: "en",
            });
    
            console.log("‚úÖ API Response to Frontend:", response.data); // Debugging response
    
            if (!response.data.current_weather || !response.data.current_weather.temperature) {
                console.error("‚ùå API response missing 'current_weather.temperature' field!");
                alert("Weather data not available.");
                return;
            }
    
            setWeatherData(response.data);
        } catch (error) {
            console.error("Error fetching weather data", error);
            alert("Failed to fetch weather data.");
        }
    };
    

    // Get user's current GPS location
    const getLocationWeather = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    fetchWeatherByCoords(latitude, longitude);
                },
                (error) => {
                    console.error("Error getting location:", error);
                    alert("Unable to retrieve your location. Please enter coordinates manually.");
                }
            );
        } else {
            alert("Geolocation is not supported by your browser.");
        }
    };

    return (
        <div>
            <input
                type="text"
                value={latitude}
                onChange={(e) => setLatitude(e.target.value)}
                placeholder="Enter latitude (e.g., 40.7128)"
            />
            <input
                type="text"
                value={longitude}
                onChange={(e) => setLongitude(e.target.value)}
                placeholder="Enter longitude (e.g., -74.006)"
            />
            <button onClick={() => fetchWeatherByCoords(latitude, longitude)}>Get Weather</button>
            <button onClick={getLocationWeather}>Get Current Location Weather</button>
            {loading && <p>Fetching weather data...</p>}
        </div>
    );
};

const fetchWeather = async () => {
    try {
        console.log("üöÄ Sending request to backend...");
        console.log("üîç Request payload:", { lat, lon, userId });

        const response = await axios.post("http://localhost:5000/api/weather/get-weather", {
            lat,
            lon,
            userId,
        });

        console.log("‚úÖ Response from backend:", response.data);
        setWeatherData(response.data);
    } catch (error) {
        console.error("‚ùå Error fetching weather data:", error.response ? error.response.data : error.message);
        alert("Failed to fetch weather data. Please check backend logs.");
    }
};


export default SearchBar;
