import React, { useState } from "react";
import SearchBar from "../components/SearchBar";

const Home = () => {
    const [weatherData, setWeatherData] = useState(null);
    const [forecastData, setForecastData] = useState(null);

    return (
        <div>
            <h1>Weather App</h1>
            <SearchBar setWeatherData={setWeatherData} setForecastData={setForecastData} />

            {/* 当前天气 */}
            {weatherData && (
                <div>
                    <h2>当前天气：{weatherData.name}</h2>
                    <p>温度：{weatherData.main.temp}°C</p>
                    <p>湿度：{weatherData.main.humidity}%</p>
                    <p>风速：{weatherData.wind.speed} km/h</p>
                    <p>天气状况：{weatherData.weather[0].description}</p>
                    <img src={`https://openweathermap.org/img/wn/${weatherData.weather[0].icon}.png`} alt="weather icon" />
                </div>
            )}

            {/* 5 天天气预报 */}
            {forecastData && (
                <div>
                    <h2>未来 5 天天气预报</h2>
                    <div style={{ display: "flex", flexWrap: "wrap" }}>
                        {forecastData.list.filter((_, index) => index % 8 === 0).map((forecast, index) => (
                            <div key={index} style={{ margin: "10px", padding: "10px", border: "1px solid gray" }}>
                                <p>{new Date(forecast.dt * 1000).toLocaleDateString()}</p>
                                <p>温度：{forecast.main.temp}°C</p>
                                <p>天气状况：{forecast.weather[0].description}</p>
                                <img src={`https://openweathermap.org/img/wn/${forecast.weather[0].icon}.png`} alt="forecast icon" />
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default Home;
