import React, { useState, useEffect } from "react";
import axios from "axios";

const WeatherHistory = () => {
    const [records, setRecords] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchType, setSearchType] = useState("recent");
    const [loading, setLoading] = useState(false);
    const [editingRecord, setEditingRecord] = useState(null);
    const [aiTourism, setAiTourism] = useState("");
    const [loadingAi, setLoadingAi] = useState(false);

    // New record state
    const [newRecord, setNewRecord] = useState({
        location: "",
        weather: "",
        temperature: "",
        wind_speed: "",
        humidity: "",
        userId: "default-user",
    });

    useEffect(() => {
        fetchWeatherHistory();
    }, []);

    // Fetch weather history
    const fetchWeatherHistory = async () => {
        setLoading(true);
        try {
            let url = "http://localhost:5000/api/weather/history";
            if (searchType !== "recent" && searchTerm.trim() !== "") {
                url = `http://localhost:5000/api/weather/history?${searchType}=${searchTerm}`;
            }
            const response = await axios.get(url);
            setRecords(response.data);
        } catch (error) {
            alert("Failed to fetch weather history.");
        }
        setLoading(false);
    };

    // Delete record
    const handleDelete = async (id) => {
        if (window.confirm("Are you sure you want to delete this record?")) {
            try {
                await axios.delete(`http://localhost:5000/api/weather/${id}`);
                fetchWeatherHistory();
            } catch (error) {
                alert("Failed to delete the record.");
            }
        }
    };

    // Edit record
    const handleEdit = (record) => {
        setEditingRecord({ ...record });
    };

    // Cancel edit
    const cancelEdit = () => {
        setEditingRecord(null);
    };

    // Update record
    const handleUpdate = async () => {
        if (!editingRecord) return;

        try {
            await axios.put(`http://localhost:5000/api/weather/${editingRecord._id}`, editingRecord);
            setEditingRecord(null);
            fetchWeatherHistory();
        } catch (error) {
            alert("Failed to update the record.");
        }
    };

    // Add new record
    const handleAdd = async () => {
        if (!newRecord.location || !newRecord.weather || !newRecord.temperature) {
            alert("Please fill in required fields.");
            return;
        }

        try {
            await axios.post("http://localhost:5000/api/weather", newRecord);
            setNewRecord({ location: "", weather: "", temperature: "", wind_speed: "", humidity: "", userId: "default-user" });
            fetchWeatherHistory();
        } catch (error) {
            alert("Failed to add a new record.");
        }
    };

    // Export CSV
    const handleExport = () => {
        window.location.href = "http://localhost:5000/api/weather/export";
    };

    // Fetch AI tourism recommendation
    const fetchAiTourism = async (location) => {
        if (!location) {
            alert("Please select a location first.");
            return;
        }

        setLoadingAi(true);
        try {
            const response = await axios.post("http://localhost:5000/api/weather/ai-tourism-suggestion", { location });
            setAiTourism(response.data.suggestions);
        } catch (error) {
            alert("Failed to fetch AI tourism recommendations.");
        }
        setLoadingAi(false);
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Weather History</h2>

            {/* Search Bar */}
            <div style={styles.searchContainer}>
                <select value={searchType} onChange={(e) => setSearchType(e.target.value)} style={styles.input}>
                    <option value="recent">Recent 10</option>
                    <option value="user">By Username</option>
                    <option value="date">By Date</option>
                    <option value="location">By Location</option>
                </select>
                <input 
                    type="text" 
                    value={searchTerm} 
                    onChange={(e) => setSearchTerm(e.target.value)} 
                    placeholder="Enter search term" 
                    disabled={searchType === "recent"} 
                    style={styles.input}
                />
                <button onClick={fetchWeatherHistory} style={styles.button}>Search</button>
                <button onClick={handleExport} style={styles.button}>Export CSV</button>
            </div>

            {/* Add New Record */}
            <h3 style={styles.subTitle}>Add New Record</h3>
            <div style={styles.addContainer}>
                <input type="text" placeholder="Location" value={newRecord.location} onChange={(e) => setNewRecord({ ...newRecord, location: e.target.value })} style={styles.input} />
                <input type="text" placeholder="Weather" value={newRecord.weather} onChange={(e) => setNewRecord({ ...newRecord, weather: e.target.value })} style={styles.input} />
                <input type="number" placeholder="Temperature (°C)" value={newRecord.temperature} onChange={(e) => setNewRecord({ ...newRecord, temperature: e.target.value })} style={styles.input} />
                <input type="number" placeholder="Wind Speed (km/h)" value={newRecord.wind_speed} onChange={(e) => setNewRecord({ ...newRecord, wind_speed: e.target.value })} style={styles.input} />
                <input type="number" placeholder="Humidity (%)" value={newRecord.humidity} onChange={(e) => setNewRecord({ ...newRecord, humidity: e.target.value })} style={styles.input} />
                <button onClick={handleAdd} style={styles.button}>Add</button>
            </div>

            {/* Weather Data Table */}
            {loading ? <p>Loading...</p> : (
                <table style={styles.table}>
                    <thead>
                        <tr>
                            <th>Queried At</th>
                            <th>Location</th>
                            <th>Weather</th>
                            <th>Temperature (°C)</th>
                            <th>Wind Speed (km/h)</th>
                            <th>Humidity (%)</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {records.map((record) => (
                            <tr key={record._id}>
                                <td>{new Date(record.queriedAt).toLocaleString()}</td>
                                <td>{record.location}</td>
                                <td>{record.current_weather.weather}</td>
                                <td>{record.current_weather.temperature}°C</td>
                                <td>{record.current_weather.wind_speed} km/h</td>
                                <td>{record.current_weather.humidity}%</td>
                                <td>
                                    <button onClick={() => handleEdit(record)} style={styles.button}>Edit</button>
                                    <button onClick={() => handleDelete(record._id)} style={styles.button}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

// Styles
const styles = {
    container: { textAlign: "center", padding: "20px" },
    table: { width: "100%", borderCollapse: "collapse" },
    input: { margin: "5px", padding: "8px" },
    button: { margin: "5px", padding: "8px", cursor: "pointer" },
    aiContainer: { marginTop: "20px", padding: "10px", border: "1px solid #ccc" },
    aiText: { fontStyle: "italic", marginTop: "10px" },
};

export default WeatherHistory;
