import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import SearchBar from "../components/SearchBar";

const Home = ({ setUser }) => {
    const [weatherData, setWeatherData] = useState(null);
    const [user, setUserState] = useState(null);
    const navigate = useNavigate();

    // Check Login State
    useEffect(() => {
        const storedUser = localStorage.getItem("user");
        if (storedUser) {
            setUserState(JSON.parse(storedUser));
        } else {
            navigate("/login"); // Redirect to Login page
        }
    }, [navigate]);

    // Logout
    const handleLogout = () => {
        navigate("/login"); // Redirect to login page
    };

    // Delete user data and logout
    const handleDelete = () => {
        localStorage.removeItem("user");
        setUser(null);
        setUserState(null);
        navigate("/login"); // Redirect to Login page
    };

    return (
        <div style={{ padding: "20px", textAlign: "center" }}>
            <h1>Weather App</h1>

            {user && (
                <div style={{ marginBottom: "20px" }}>
                    <p>Welcome, <strong>{user.username}</strong>!</p>
                    <button onClick={handleLogout}>Logout</button>
                    <button onClick={handleDelete} style={{ marginLeft: "10px", color: "red" }}>Delete Account</button>
                </div>
            )}

            <SearchBar setWeatherData={setWeatherData} />

            {weatherData && weatherData.current_weather ? (
                <div>
                    <h2>{weatherData.location || "Unknown Location"}'s Weather</h2>
                    
                    {/* Weather Data Display in 1x3 Grid */}
                    <div style={{ display: "flex", justifyContent: "space-between", marginTop: "20px" }}>
                        
                        {/* Current Weather */}
                        <div style={cardStyle}>
                            <h3>Current Weather</h3>
                            <p><strong>{weatherData.current_weather?.weather || "N/A"}</strong></p>
                            <p>🌡️ Temp: {weatherData.current_weather?.temperature || "N/A"}°C</p>
                            <p>💧 Humidity: {weatherData.current_weather?.humidity || "N/A"}%</p>
                            <p>💨 Wind: {weatherData.current_weather?.wind_speed || "N/A"} km/h</p>
                        </div>

                        {/* Past 3 Days Weather */}
                        <div style={cardStyle}>
                            <h3>Past 3 Days</h3>
                            {weatherData.past_weather?.length > 0 ? (
                                <ul style={{ listStyle: "none", padding: 0 }}>
                                    {weatherData.past_weather.slice(0, 3).map((day, index) => (
                                        <li key={index}>
                                            <strong>{day.date}</strong>: {day.temperature_max}°C / {day.temperature_min}°C 🌧 {day.precipitation}mm
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p>No past data available.</p>
                            )}
                        </div>

                        {/* Future 2 Days Weather */}
                        <div style={cardStyle}>
                            <h3>Next 2 Days</h3>
                            {weatherData.future_weather?.length > 0 ? (
                                <ul style={{ listStyle: "none", padding: 0 }}>
                                    {weatherData.future_weather.slice(0, 2).map((day, index) => (
                                        <li key={index}>
                                            <strong>{day.date}</strong>: {day.weather || "N/A"} 🌡 {day.temperature || "N/A"}°C
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p>No future data available.</p>
                            )}
                        </div>
                    </div>
                </div>
            ) : (
                <p>Enter coordinates and fetch weather data.</p>
            )}
        </div>
    );
};

// Card Styling
const cardStyle = {
    flex: "1",
    margin: "10px",
    padding: "15px",
    borderRadius: "10px",
    backgroundColor: "#f9f9f9",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
    textAlign: "center",
};

export default Home;
